import React, { useState } from 'react';
import './Teams.css';

const Teams = () => {
  const [messages, setMessages] = useState([
    // ...existing messages
  ]);
  const [files, setFiles] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [replyTo, setReplyTo] = useState(null); // Track which message we're replying to

  const handleSend = () => {
    if (newMessage.trim() !== '') {
      const nextId = messages.length + 1;
      let replyText = newMessage;
      if (replyTo) {
        const originalMessage = messages.find(message => message.id === replyTo);
        replyText = `Replying to "${originalMessage.content}": ${newMessage}`;
        setReplyTo(null); // Reset replyTo state
      }
      setMessages([...messages, { id: nextId, author: 'You', content: replyText, type: 'sent' }]);
      setNewMessage('');
    }
  };

  const handleFileUpload = (event) => {
    const uploadedFiles = event.target.files;
    const fileList = Array.from(uploadedFiles).map((file) => ({
      name: file.name,
      size: file.size,
      url: URL.createObjectURL(file) // Create a URL for each uploaded file
    }));
    setFiles(files.concat(fileList));
  };

  const handleReply = (messageId) => {
    setReplyTo(messageId); // Set the message ID that is being replied to
    const originalMessage = messages.find(message => message.id === messageId);
    setNewMessage(`@${originalMessage.author}: `); // Pre-populate the chat input with the user's name
  };

  return (
    <div className="chat-container">
      {/* ...tabs and chat-messages */}
      <div className="files-list">
        {files.map((file, index) => (
          <div key={index} className="file-item">
            <a href={file.url} target="_blank" rel="noopener noreferrer" download>
              {file.name} ({Math.round(file.size / 1024)} KB)
            </a>
          </div>
        ))}
      </div>
      <div className="chat-input">
        <input
          type="text"
          placeholder={replyTo ? `Replying to message #${replyTo}: ` : "Type something ..."}
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
        />
        <button className="send-btn" onClick={handleSend}>Send</button>
      </div>
    </div>
  );
};

export default Teams;
