import React, { useState } from 'react';
import './Teams.css';

const Teams = () => {
  const [messages, setMessages] = useState([
    //...existing messages
  ]);
  const [files, setFiles] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const handleSend = () => {
    if (newMessage.trim() !== '') {
      const nextId = messages.length + 1;
      setMessages([...messages, { id: nextId, author: 'You', content: newMessage, type: 'sent' }]);
      setNewMessage('');
    }
  };

  const handleFileUpload = (event) => {
    const uploadedFiles = event.target.files;
    const fileList = Array.from(uploadedFiles).map(file => ({
      id: files.length + 1,
      name: file.name,
      size: file.size,
      type: 'file',
      content: `Uploaded ${file.name}`,
      author: 'You'
    }));

    // Add the uploaded file details to the messages array
    setMessages([...messages, ...fileList]);
    setFiles([...files, ...fileList]);  // If you want to keep a separate files array
  };

  // ...other component logic

  return (
    <div className="chat-container">
      {/* ...tabs and other UI elements */}
      <div className="chat-messages">
        {messages.map((message) => (
          <div key={message.id} className={`message ${message.type}`}>
            <div className="initials">{message.author.split(' ').map(name => name[0]).join('')}</div>
            <div className="text">{message.type === 'file' ? `ðŸ“„ ${message.content}` : message.content}</div>
            {message.type === 'received' && <button className="reply-btn">Reply</button>}
          </div>
        ))}
      </div>
      {/* ...input and send button */}
    </div>
  );
};

export default Teams;
