import React, { useState } from 'react';
import './Profile.css';

const Profile = () => {
  const [editMode, setEditMode] = useState(false);
  const [profile, setProfile] = useState({
    name: 'Vikash Dubey',
    empId: 'f,vf333333333333',
    email: 'vikash7618@gmail.com',
    phone: '09146030932',
    department: 'it',
    photo: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfile({ ...profile, [name]: value });
  };

  const handleImageChange = (e) => {
    if (e.target.files[0]) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setProfile({ ...profile, photo: e.target.result });
      };
      reader.readAsDataURL(e.target.files[0]);
    }
  };

  const handleToggleEdit = () => {
    setEditMode(!editMode);
  };

  const handleSave = (e) => {
    e.preventDefault();
    // Save logic here
    setEditMode(false);
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        {editMode ? (
          <>
            <input type="file" onChange={handleImageChange} className="photo-upload-input" />
            {profile.photo && <img src={profile.photo} alt="Profile" className="profile-photo" />}
          </>
        ) : (
          <div className="profile-photo-container" style={{ backgroundImage: `url(${profile.photo})` }}></div>
        )}
        <div className="name-edit-container">
          {editMode ? (
            <input
              type="text"
              value={profile.name}
              name="name"
              onChange={handleInputChange}
              placeholder="Your Name"
              className="name-input"
            />
          ) : (
            <h1 className="profile-name">{profile.name}</h1>
          )}
          <span className="edit-link" onClick={handleToggleEdit}>{editMode ? 'Save' : 'Edit'}</span>
        </div>
        {!editMode && <div className="status-indicator">Active</div>}
        <div className="local-time">{new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</div>
      </div>
      <form onSubmit={handleSave} className="profile-info">
        {/* Other profile information inputs */}
        {/* ... */}
      </form>
    </div>
  );
};

export default Profile;
