import React, { useState } from 'react';
import './Teams.css';

const Teams = () => {
  const [messages, setMessages] = useState([
    // ...existing messages
  ]);
  const [newMessage, setNewMessage] = useState('');
  const [replyingTo, setReplyingTo] = useState(null);

  const handleSend = () => {
    if (newMessage.trim() !== '') {
      const nextId = messages.length + 1;
      let replyText = newMessage;
      if (replyingTo) {
        const originalMessage = messages.find(message => message.id === replyingTo);
        replyText = `Replying to "${originalMessage.content}": ${newMessage}`;
      }
      setMessages([...messages, { id: nextId, author: 'You', content: replyText, type: 'sent' }]);
      setNewMessage('');
      setReplyingTo(null);
    }
  };

  const handleReply = (messageId) => {
    const messageToReply = messages.find(message => message.id === messageId);
    setReplyingTo(messageId);
    setNewMessage(`@${messageToReply.author}: `);
    // Optionally, you could scroll the input into view or focus it
  };

  const handleFileUpload = (event) => {
    const uploadedFiles = event.target.files;
    if (uploadedFiles.length > 0) {
      for (const file of uploadedFiles) {
        const reader = new FileReader();
        reader.onload = (e) => {
          const newFile = {
            name: file.name,
            size: file.size,
            type: file.type,
            src: e.target.result,
          };
          setFiles(files => [...files, newFile]);
        };
        reader.readAsDataURL(file);
      }
    }
  };

  const [files, setFiles] = useState([]);

  return (
    <div className="chat-container">
      {/* ...tabs and chat messages... */}
      <div className="files-list">
        {files.map((file, index) => (
          <div key={index} className="file-item">
            {file.type.startsWith('image/') ? (
              <a href={file.src} target="_blank" rel="noopener noreferrer">
                {file.name} (click to view)
              </a>
            ) : (
              <a href={file.src} download={file.name}>
                {file.name} (click to download)
              </a>
            )}
            <span className="file-size"> ({Math.round(file.size / 1024)} KB)</span>
          </div>
        ))}
      </div>
      {/* ...input and send button... */}
    </div>
  );
};

export default Teams;
