import React from 'react';
import './Dashboard.css'; // Ensure you link the CSS file

const Dashboard = () => {
  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>OVERVIEW</h1>
      </div>
      
      <div className="content-area">
        <div className="priority-tasks">
          <h2>PRIORITY TASKS</h2>
          {/* Render your priority tasks here */}
        </div>
        
        <div className="lower-section">
          <div className="task-analytics">
            <h2>TASK ANALYTICS</h2>
            {/* Render your task analytics chart here */}
          </div>
          
          <div className="calendar-section">
            <h2>CALENDAR</h2>
            {/* Render your calendar here */}
          </div>
        </div>

        <div className="report-section">
          <button>View reports here</button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
