import React, { useState } from 'react';
import './Teams.css';

const Teams = () => {
  const [messages, setMessages] = useState([
    // Pre-existing messages
  ]);
  const [newMessage, setNewMessage] = useState('');
  const [files, setFiles] = useState([]);

  const handleSend = () => {
    const trimmedMessage = newMessage.trim();
    if (trimmedMessage) {
      setMessages([...messages, { author: 'You', content: trimmedMessage, type: 'sent' }]);
      setNewMessage('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  const handleFileUpload = (e) => {
    const newFiles = e.target.files;
    if (newFiles) {
      const updatedFiles = [...files];
      for (let file of newFiles) {
        updatedFiles.push({
          name: file.name,
          url: URL.createObjectURL(file),
        });
      }
      setFiles(updatedFiles);
    }
  };

  const handleReply = (author) => {
    setNewMessage(`@${author} `);
    // Autofocus on the message input field if desired
    // This may require useRef and focusing on the input element
  };

  return (
    <div className="chat-container">
      <div className="tabs">
        <div className="tab active">Chats</div>
        <div className="tab">
          Files
          <input type="file" id="fileInput" multiple onChange={handleFileUpload} hidden />
        </div>
      </div>
      <div className="chat-messages">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.type}`}>
            <span className="initials">{msg.author[0]}</span>
            <p className="text">{msg.content}</p>
            {msg.type === 'received' && (
              <button className="reply-btn" onClick={() => handleReply(msg.author)}>Reply</button>
            )}
          </div>
        ))}
        {files.map((file, index) => (
          <div key={index} className="file-item">
            <a href={file.url} download target="_blank" rel="noopener noreferrer">
              {file.name}
            </a>
          </div>
        ))}
      </div>
      <div className="chat-input">
        <input
          type="text"
          placeholder="Type something ..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          onKeyPress={handleKeyPress}
        />
        <button className="send-btn" onClick={handleSend}>Send</button>
      </div>
    </div>
  );
};

export default Teams;
