import React from 'react';
import './Tasks.css'; // Assuming you have a separate CSS file for styles

const taskData = [
  { name: 'Marketing strategy', progress: 50, dueDate: '4 d', remaining: '-04:00', status: 'in-progress' },
  { name: 'New contract template', progress: 20, dueDate: 'Tomorrow', remaining: '-03:30', status: 'needs-review' },
  // ... add other tasks similarly
];

const progressBarClasses = {
  'in-progress': 'in-progress',
  'needs-review': 'needs-review',
  'needs-attention': 'needs-attention',
  'needs-input': 'needs-input',
  'planned': 'planned'
};

const Tasks = () => {
  return (
    <div className="dashboard">
      <h1>High Priority</h1>
      {taskData.map((task, index) => (
        <div key={index} className="task">
          <input type="checkbox" />
          <div className="task-name">{task.name}</div>
          <div className="progress-bar">
            <div className={progressBarClasses[task.status]} style={{ width: `${task.progress}%` }}></div>
          </div>
          <div>{task.dueDate}</div>
          <div>{task.remaining}</div>
        </div>
      ))}
    </div>
  );
};

export default Tasks;
