import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Add this import
import './SignupApp.css';

const SignupApp = () => { // Change the component name to match the filename
  const navigate = useNavigate(); // Hook to programmatically navigate
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [signUpStatus, setSignUpStatus] = useState('');

  const handleSignUp = (event) => {
    event.preventDefault();
    setSignUpStatus('Signing up...');

    fetch('http://localhost:3001/signup', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    })
    .then(response => {
      if (!response.ok) {
        if (response.status === 409) {
          throw new Error('User already exists');
        }
        throw new Error(`Signup failed: ${response.status}`);
      }
      return response.text();
    })
    .then(data => {
      setSignUpStatus('Signup successful!');
      navigate('/dashboard'); // Redirect to the dashboard or the appropriate route in App.js
    })
    .catch(error => {
      setSignUpStatus(error.message);
    });
  };

  // Your JSX code remains the same
};

export default SignupApp;
