import React, { useState } from 'react';
import './Teams.css';

const Chat = () => {
  const [messages, setMessages] = useState([
    { id: 1, author: "Sharone S", content: "How are the designs coming along?", initial: "SS" },
    { id: 2, author: "Rishi Kaur", content: "Good!", initial: "RK" },
    { id: 3, author: "Anjali Kumar", content: "Please go through all files before submissions", initial: "AK" },
    // ... more messages
  ]);
  const [newMessage, setNewMessage] = useState('');

  const handleSend = () => {
    // Add logic to send message
    const newMsg = { id: messages.length + 1, author: "You", content: newMessage, initial: "Y" };
    setMessages([...messages, newMsg]);
    setNewMessage('');
  };

  return (
    <div className="chat-container">
      <div className="tabs">
        <div className="tab active">Chats</div>
        <div className="tab">Files</div>
      </div>
      <div className="chat-window">
        {messages.map((msg) => (
          <div key={msg.id} className="message">
            <div className="message-initials">{msg.initial}</div>
            <div className="message-content">
              <div className="message-author">{msg.author}</div>
              <div className="message-body">{msg.content}</div>
            </div>
            <div className="message-reply">Reply</div>
          </div>
        ))}
      </div>
      <div className="chat-input">
        <input
          type="text"
          placeholder="type something ..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
        />
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  );
};

export default Chat;
