import React, { useState } from 'react';
import './Teams.css';

const Teams = () => {
  const [messages, setMessages] = useState([
    { id: 1, author: 'Sharon S', content: 'How are the designs coming along?', type: 'received' },
    // ... other messages
  ]);
  const [files, setFiles] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [replyTo, setReplyTo] = useState(null);

  const handleSend = () => {
    if (newMessage.trim() !== '') {
      const nextId = messages.length + 1;
      setMessages([...messages, { id: nextId, author: 'You', content: newMessage, type: 'sent', replyTo }]);
      setNewMessage('');
      setReplyTo(null); // Reset replyTo state after sending the message
    }
  };

  const handleFileUpload = (event) => {
    const uploadedFiles = event.target.files;
    const fileList = Array.from(uploadedFiles).map((file) => ({
      name: file.name,
      size: file.size,
      url: URL.createObjectURL(file),
    }));
    setFiles(files.concat(fileList));
  };

  const handleReply = (author) => {
    setReplyTo(author); // Set the author to whom you are replying
    setNewMessage(`@${author.split(' ')[0]} `);
    // Focus the chat input after setting the reply. You would need to give your input a ref.
    document.querySelector('.chat-input input').focus();
  };

  return (
    <div className="chat-container">
      {/* ...tabs and other elements */}
      
      {/* File input for uploading files */}
      <input id="fileUpload" type="file" multiple onChange={handleFileUpload} style={{ display: 'none' }} />

      {/* Display uploaded files */}
      <div className="files-list">
        {files.map((file, index) => (
          <div key={index} className="file-item">
            <a href={file.url} target="_blank" rel="noopener noreferrer" download={file.name}>
              {file.name} ({Math.round(file.size / 1024)} KB)
            </a>
          </div>
        ))}
      </div>
      
      {/* Messages */}
      <div className="chat-messages">
        {messages.map((message) => (
          <div key={message.id} className={`message ${message.type}`}>
            <div className="initials">{message.author.split(' ').map(name => name[0]).join('')}</div>
            <div className="text">{message.content}</div>
            {message.type === 'received' && <button className="reply-btn" onClick={() => handleReply(message.author)}>Reply</button>}
          </div>
        ))}
      </div>
      
      {/* Chat input */}
      <div className="chat-input">
        <input
          type="text"
          placeholder="type something ..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
        />
        <button className="send-btn" onClick={handleSend}>Send</button>
      </div>
    </div>
  );
};

export default Teams;
