import React, { useState } from 'react';
import './Profile.css';

const Profile = () => {
  const [editMode, setEditMode] = useState(false);
  const [profile, setProfile] = useState({
    name: '',
    empId: '',
    email: '',
    phone: '',
    department: '',
    photo: null // Add a new state to hold the photo
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfile({ ...profile, [name]: value });
  };

  const handleImageChange = (e) => {
    if (e.target.files[0]) {
      const fileReader = new FileReader();
      fileReader.readAsDataURL(e.target.files[0]);
      fileReader.onload = () => {
        setProfile({ ...profile, photo: fileReader.result });
      };
    }
  };

  const handleToggleEdit = () => {
    setEditMode(!editMode);
  };

  const handleSave = (e) => {
    e.preventDefault();
    // Implement save logic
    setEditMode(false);
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-photo-container">
          {profile.photo ? (
            <img src={profile.photo} alt="Profile" className="profile-photo" />
          ) : (
            <div className="profile-photo-placeholder"></div>
          )}
          {editMode && (
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
              className="upload-photo-btn"
            />
          )}
        </div>
        <div className="name-edit-container">
          {editMode ? (
            <input
              type="text"
              value={profile.name}
              name="name"
              onChange={handleInputChange}
              placeholder="Your Name"
            />
          ) : (
            <>
              <h1>{profile.name || "Your Name"}</h1>
              <span className="edit-link" onClick={handleToggleEdit}>
                {editMode ? 'Save' : 'Edit'}
              </span>
            </>
          )}
        </div>
        <div className="status-section">
          <span className="status-indicator"></span>
          Active
          <span className="local-time">
            {new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
          </span>
        </div>
      </div>
      {editMode ? (
        <form onSubmit={handleSave} className="profile-info">
          <input
            type="text"
            value={profile.empId}
            name="empId"
            onChange={handleInputChange}
            placeholder="Employee ID"
          />
          <input
            type="email"
            value={profile.email}
            name="email"
            onChange={handleInputChange}
            placeholder="Email"
          />
          <input
            type="tel"
            value={profile.phone}
            name="phone"
            onChange={handleInputChange}
            placeholder="Phone"
          />
          <input
            type="text"
            value={profile.department}
            name="department"
            onChange={handleInputChange}
            placeholder="Department"
          />
          <button type="submit" className="save-btn">Save</button>
        </form>
      ) : (
        <div className="profile-info-display">
          <p>Employee ID: {profile.empId}</p>
          <p>Email: {profile.email}</p>
          <p>Phone: {profile.phone}</p>
          <p>Department: {profile.department}</p>
        </div>
      )}
    </div>
  );
};

export default Profile;
