import React, { useState } from 'react';
import './Profile.css';

const Profile = () => {
  // Profile data state
  const [profileData, setProfileData] = useState({
    name: '',
    empId: '',
    email: '',
    phone: '',
    department: '',
  });

  // State to handle edit mode
  const [isEditMode, setIsEditMode] = useState(false);

  // Update profile data
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfileData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  // Toggle edit mode
  const toggleEditMode = () => {
    setIsEditMode(!isEditMode);
  };

  // Get local time
  const localTime = new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });

  return (
    <div className="profile-wrapper">
      <div className="profile-card">
        <div className="profile-image-section">
          <div className="profile-image"></div>
          <button className="upload-button">Upload Photo</button>
        </div>
        <div className="profile-info">
          {isEditMode ? (
            <input
              type="text"
              name="name"
              value={profileData.name}
              onChange={handleInputChange}
              placeholder="Your Name"
            />
          ) : (
            <h1>{profileData.name || "Your Name"}</h1>
          )}
          <div className="status-section">
            <span className="status-indicator"></span> Active
          </div>
          <p>{localTime}</p>
        </div>
        <button className="edit-button" onClick={toggleEditMode}>
          {isEditMode ? 'Save' : 'Edit'}
        </button>
        {isEditMode ? (
          <div className="editable-fields">
            <input
              type="text"
              name="empId"
              value={profileData.empId}
              onChange={handleInputChange}
              placeholder="Employee ID"
            />
            <input
              type="email"
              name="email"
              value={profileData.email}
              onChange={handleInputChange}
              placeholder="Email"
            />
            <input
              type="tel"
              name="phone"
              value={profileData.phone}
              onChange={handleInputChange}
              placeholder="Phone"
            />
            <input
              type="text"
              name="department"
              value={profileData.department}
              onChange={handleInputChange}
              placeholder="Department"
            />
          </div>
        ) : (
          <>
            <p>Employee ID: {profileData.empId}</p>
            <p>Email: {profileData.email}</p>
            <p>Phone: {profileData.phone}</p>
            <p>Department: {profileData.department}</p>
          </>
        )}
      </div>
    </div>
  );
};

export default Profile;
