import React, { useState, useEffect } from 'react';
import './Profile.css';

const Profile = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [profileData, setProfileData] = useState({
    name: '',
    empId: '',
    email: '',
    phone: '',
    department: '',
  });

  // This effect could be used to update the local time periodically
  useEffect(() => {
    const intervalId = setInterval(() => {
      setProfileData((data) => ({ ...data, localTime: new Date().toLocaleTimeString() }));
    }, 1000);

    // Cleanup the interval on component unmount
    return () => clearInterval(intervalId);
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfileData({ ...profileData, [name]: value });
  };

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleSaveClick = () => {
    setIsEditing(false);
    // Here you would handle form submission, like sending data to a server
    console.log(profileData);
  };

  return (
    <div className="profile-wrapper">
      <aside className="sidebar"> {/* Sidebar code goes here */} </aside>
      <main className="profile-content">
        <section className="profile">
          <div className="profile-photo-section">
            <div className="profile-photo" />
            {isEditing && <button className="upload-button">Upload Photo</button>}
          </div>
          {isEditing ? (
            <input
              type="text"
              name="name"
              value={profileData.name}
              placeholder="Your Name"
              onChange={handleChange}
            />
          ) : (
            <h1>{profileData.name || "Your Name"}</h1>
          )}
          {isEditing ? (
            <>
              <div className="form-field">
                <label>Employee ID</label>
                <input
                  type="text"
                  name="empId"
                  value={profileData.empId}
                  onChange={handleChange}
                />
              </div>
              {/* Additional fields for email, phone, and department */}
              {/* ... */}
              <button onClick={handleSaveClick}>Save</button>
            </>
          ) : (
            <>
              <p>Employee ID: {profileData.empId}</p>
              {/* Display additional fields for email, phone, and department */}
              {/* ... */}
              <button onClick={handleEditClick}>Edit</button>
            </>
          )}
          <div className="status-section">
            <span className="status-indicator" />
            <span className="status-label">Active</span>
            <span className="local-time">{profileData.localTime}</span>
          </div>
        </section>
      </main>
    </div>
  );
};

export default Profile;
