import React, { useState } from 'react';
import './Tasks.css';

const initialTasks = [
  { id: 1, name: 'Marketing strategy', status: 'In progress', dueDate: '4 d', remaining: '-04:00' },
  { id: 2, name: 'New contract template', status: 'Needs review', dueDate: 'Tomorrow', remaining: '-03:30' },
  { id: 3, name: 'New estimation for Fineline Inc. project', status: 'Needs attention', dueDate: 'Sat, 12/12', remaining: '00:00' },
  { id: 4, name: 'Quarter budget analysis', status: 'Needs input', dueDate: 'Sat, 19/12', remaining: '00:00' },
  { id: 5, name: 'Launch marketing campaign', status: 'In progress', dueDate: 'Mon, 11/01/2021', remaining: '00:00' },
  { id: 6, name: 'Planning and data collection', status: 'Planned', dueDate: '3:20', remaining: '+01:10' }
];

const Tasks = () => {
  const [tasks, setTasks] = useState(initialTasks);

  // Function to toggle task status
  const toggleStatus = (taskId) => {
    setTasks(tasks.map(task => {
      if (task.id === taskId) {
        task.status = task.status === 'In progress' ? 'Completed' : 'In progress';
      }
      return task;
    }));
  };

  return (
    <div className="task-container">
      <h1>High Priority</h1>
      <div className="task-list">
        {tasks.map(task => (
          <div key={task.id} className="task-item">
            <input type="checkbox" checked={task.status === 'Completed'} onChange={() => toggleStatus(task.id)} />
            <span className={`task-status ${task.status.replace(/\s+/g, '-').toLowerCase()}`}>{task.status}</span>
            <span className="task-name">{task.name}</span>
            <span className="task-dueDate">{task.dueDate}</span>
            <span className="task-remaining">{task.remaining}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Tasks;
