import React, { useState } from 'react';
import './Teams.css';

const Teams = () => {
  const [messages, setMessages] = useState([
    { id: 1, author: 'Sharon S', content: 'How are the designs coming along?', type: 'received' },
    // ...other messages
  ]);
  const [files, setFiles] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const handleSend = () => {
    if (newMessage.trim() !== '') {
      const nextId = messages.length + 1;
      setMessages([...messages, { id: nextId, author: 'You', content: newMessage, type: 'sent' }]);
      setNewMessage('');
    }
  };

  const handleFileUpload = (event) => {
    const uploadedFiles = event.target.files;
    const fileList = Array.from(uploadedFiles).map((file) => {
      return {
        name: file.name,
        size: file.size,
        url: URL.createObjectURL(file),
      };
    });
    setFiles(files.concat(fileList));
  };

  const handleReply = (author) => {
    setNewMessage(`@${author.split(' ')[0]} `); // Pre-populate the input with the author's name
    // In a real application, you would focus the input field after setting the reply text.
  };

  return (
    <div className="chat-container">
      {/* ...tabs and other elements */}
      
      {/* Display uploaded files */}
      <div className="files-list">
        {files.map((file, index) => (
          <div key={index} className="file-item">
            <a href={file.url} download={file.name}>
              {file.name} ({Math.round(file.size / 1024)} KB)
            </a>
          </div>
        ))}
      </div>
      
      {/* Messages */}
      <div className="chat-messages">
        {/* ...messages */}
      </div>
      
      {/* Chat input */}
      <div className="chat-input">
        <input
          type="text"
          placeholder="type something ..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
        />
        <button className="send-btn" onClick={handleSend}>Send</button>
      </div>
    </div>
  );
};

export default Teams;
