import React, { useState } from 'react';
import './Profile.css';

const Profile = () => {
  const [profile, setProfile] = useState({
    name: 'Vikash Dubey',
    empId: '',
    email: '',
    phone: '',
    department: '',
    photo: null,
  });
  const [editMode, setEditMode] = useState(false);
  
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfile(prevProfile => ({
      ...prevProfile,
      [name]: value
    }));
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setProfile(prevProfile => ({
        ...prevProfile,
        photo: URL.createObjectURL(file)
      }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission
    console.log(profile);
    setEditMode(false);
  };

  return (
    <div className="profile-wrapper">
      <div className="profile-card">
        <div className="profile-photo-section">
          {profile.photo ? (
            <img src={profile.photo} alt="Profile" className="profile-photo" />
          ) : (
            <div className="profile-photo-placeholder">
              {editMode && <input type="file" onChange={handlePhotoChange} />}
            </div>
          )}
        </div>
        <div className="profile-info-section">
          {editMode ? (
            <input 
              type="text"
              name="name"
              value={profile.name}
              onChange={handleInputChange}
              className="profile-name-input"
            />
          ) : (
            <div className="profile-name">{profile.name}</div>
          )}
          {editMode && (
            <>
              <input 
                type="text"
                name="empId"
                placeholder="Employee ID"
                value={profile.empId}
                onChange={handleInputChange}
              />
              <input 
                type="email"
                name="email"
                placeholder="Email"
                value={profile.email}
                onChange={handleInputChange}
              />
              <input 
                type="tel"
                name="phone"
                placeholder="Phone"
                value={profile.phone}
                onChange={handleInputChange}
              />
              <input 
                type="text"
                name="department"
                placeholder="Department"
                value={profile.department}
                onChange={handleInputChange}
              />
              <button onClick={handleSubmit} className="save-btn">Save</button>
            </>
          )}
          {!editMode && <button onClick={() => setEditMode(true)} className="edit-btn">Edit</button>}
          <div className="profile-status">Active - {new Date().toLocaleTimeString()}</div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
