import React, { useState } from 'react';
import './Teams.css';

const Teams = () => {
  const [messages, setMessages] = useState([
    { id: 1, author: 'Sharon S', content: 'How are the designs coming along?', type: 'received' },
    // ... other messages
  ]);
  const [files, setFiles] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [replyingTo, setReplyingTo] = useState(null); // Keep track of which message we're replying to

  const handleSend = () => {
    if (newMessage.trim() !== '') {
      const nextId = messages.length + 1;
      let replyContent = newMessage;
      if (replyingTo) {
        const originalMessage = messages.find(message => message.id === replyingTo);
        replyContent = `Replying to "${originalMessage.content}": ${newMessage}`;
      }
      setMessages([...messages, { id: nextId, author: 'You', content: replyContent, type: 'sent' }]);
      setNewMessage('');
      setReplyingTo(null); // Reset reply state after sending
    }
  };

  const handleFileUpload = (event) => {
    const uploadedFiles = event.target.files;
    for (let file of uploadedFiles) {
      const fileReader = new FileReader();
      fileReader.onload = (e) => {
        setFiles(files.concat({
          name: file.name,
          size: file.size,
          type: file.type,
          src: e.target.result, // This is the data URL
        }));
      };
      fileReader.readAsDataURL(file);
    }
  };

  const handleReply = (messageId) => {
    setReplyingTo(messageId);
    const messageToReply = messages.find(message => message.id === messageId);
    setNewMessage(`@${messageToReply.author}: `); // Prefill the message input with the author's name
  };

  return (
    <div className="chat-container">
      {/* ... other UI elements ... */}
      <div className="chat-messages">
        {/* ... existing message mapping ... */}
        <div className="files-list">
          {files.map((file, index) => (
            <div key={index} className="file-item">
              {file.name} - {Math.round(file.size / 1024)} KB
              {' '}(<a href={file.src} target="_blank" rel="noopener noreferrer">view</a>)
            </div>
          ))}
        </div>
      </div>
      <div className="chat-input">
        <input 
          type="text" 
          placeholder={replyingTo ? "Type your reply..." : "type something ..."} 
          value={newMessage} 
          onChange={(e) => setNewMessage(e.target.value)} 
        />
        <button className="send-btn" onClick={handleSend}>Send</button>
      </div>
    </div>
  );
};

export default Teams;
