import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './login.css';

const SignupApp = () => {
  const navigate = useNavigate();
  const [isLogin, setIsLogin] = useState(true); // State to toggle between login and signup
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [status, setStatus] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    const url = `http://localhost:3001/${isLogin ? 'login' : 'signup'}`;
    const statusText = isLogin ? 'login...' : 'signing up...';
    setStatus(statusText);

    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      if (!response.ok) {
        throw new Error(`${isLogin ? 'Login' : 'Signup'} failed: ${response.status}`);
      }

      setStatus(isLogin ? 'login successful!' : 'signup successful!');
      navigate('/dashboard');
    } catch (error) {
      setStatus(error.message);
    }
  };

  return (
    <div className="app">
      <div className="illustration" />
      <div className="form-container">
        <form className="signup-form" onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="username">USERNAME</label>
            <input
              type="text"
              id="username"
              name="username"
              value={username}
              onChange={e => setUsername(e.target.value)}
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">PASSWORD</label>
            <input
              type="password"
              id="password"
              name="password"
              value={password}
              onChange={e => setPassword(e.target.value)}
            />
          </div>
          <button type="submit">{isLogin ? 'LOG IN' : 'SIGN UP'}</button>
          <button 
            type="button" 
            className="toggle-btn" 
            onClick={() => setIsLogin(!isLogin)}
          >
            {isLogin ? 'Create a new account' : 'Go to login'}
          </button>
        </form>
        {status && <p className="status-message">{status}</p>}
      </div>
    </div>
  );
};

export default SignupApp;
