import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './login.css';

const SignupApp = () => {
  const navigate = useNavigate();
  const [isLogin, setIsLogin] = useState(true); // State to toggle between login and signup
  const [isSignedUp, setIsSignedUp] = useState(false); // New state to check if user has signed up
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [status, setStatus] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    if (!isLogin && password !== confirmPassword) {
      setStatus('Passwords do not match.');
      return;
    }

    const endpoint = isLogin ? 'login' : 'signup';
    const url = `http://localhost:3001/${endpoint}`;
    setStatus(endpoint === 'login' ? 'Logging in...' : 'Signing up...');
    const body = JSON.stringify({ username, password, ...(endpoint === 'signup' && { confirmPassword }) });

    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: body,
      });

      if (!response.ok) {
        throw new Error(`${endpoint} failed: ${response.status}`);
      }

      if (isLogin) {
        setStatus('Login successful!');
        navigate('/dashboard');
      } else {
        setStatus('Signup successful! Please log in.');
        setIsSignedUp(true); // User has signed up
        setIsLogin(true); // Show login form next
      }
    } catch (error) {
      setStatus(error.message);
    }
  };

  // Redirect user to login form after successful signup
  if (isSignedUp) {
    return (
      <div className="app">
        <div className="status-message">
          <p>Signup successful! Please log in with your new credentials.</p>
          <button onClick={() => setIsLogin(true)}>Go to Login</button>
        </div>
      </div>
    );
  }

  return (
    <div className="app">
      {/* ...rest of your component */}
    </div>
  );
};

export default SignupApp;
