import React, { useState } from 'react';
import './Teams.css';

const Teams = () => {
  const [messages, setMessages] = useState([
    { id: 1, author: 'Sharon S', content: 'How are the designs coming along?', type: 'received' },
    // ...other initial messages
  ]);
  const [files, setFiles] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const sendMessage = (content, isFile = false) => {
    const nextId = messages.length + 1;
    setMessages([
      ...messages,
      {
        id: nextId,
        author: 'You',
        content,
        type: 'sent',
        isFile,
      },
    ]);
  };

  const handleSend = () => {
    if (newMessage.trim() !== '') {
      sendMessage(newMessage);
      setNewMessage('');
    }
  };

  const handleFileUpload = (event) => {
    const uploadedFiles = event.target.files;
    if (uploadedFiles.length) {
      const newFiles = Array.from(uploadedFiles).map((file) => ({
        name: file.name,
        size: file.size,
        url: URL.createObjectURL(file),
      }));

      setFiles([...files, ...newFiles]);

      // For each uploaded file, send it as a message in the chat
      newFiles.forEach((file) => {
        sendMessage(`Uploaded ${file.name}`, true);
      });
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  // ...rest of the component

  return (
    <div className="chat-container">
      {/* ...tabs and other elements */}
      
      <div className="chat-messages">
        {/* Messages and files rendering */}
        {/* ... */}
      </div>
      
      <div className="chat-input">
        <input 
          type="text" 
          placeholder="type something ..." 
          value={newMessage} 
          onChange={(e) => setNewMessage(e.target.value)} 
          onKeyPress={handleKeyPress} 
        />
        <button className="send-btn" onClick={handleSend}>Send</button>
      </div>
      
      <input
        id="fileUpload"
        type="file"
        multiple
        onChange={handleFileUpload}
        style={{ display: 'none' }}
      />
    </div>
  );
};

export default Teams;
