import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './login.css';

const SignupApp = () => {
  const navigate = useNavigate();
  const [isLogin, setIsLogin] = useState(true); // State to toggle between login and signup
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState(''); // For signup confirmation password
  const [status, setStatus] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    let url, body;
    
    if (isLogin) {
      url = 'http://localhost:3001/login';
      body = JSON.stringify({ username, password });
    } else {
      // For signup, make sure passwords match
      if (password !== confirmPassword) {
        setStatus('Passwords do not match.');
        return;
      }
      url = 'http://localhost:3001/signup';
      body = JSON.stringify({ username, password, confirmPassword });
    }

    setStatus(isLogin ? 'Logging in...' : 'Signing up...');

    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: body,
      });

      if (!response.ok) {
        throw new Error(`${isLogin ? 'Login' : 'Signup'} failed: ${response.status}`);
      }

      setStatus(isLogin ? 'Login successful!' : 'Signup successful!');
      navigate('/dashboard');
    } catch (error) {
      setStatus(error.message);
    }
  };

  return (
    <div className="app">
      <div className="illustration" />
      <div className="form-container">
        <form className="signup-form" onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="username">USERNAME</label>
            <input
              type="text"
              id="username"
              name="username"
              value={username}
              onChange={e => setUsername(e.target.value)}
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">PASSWORD</label>
            <input
              type="password"
              id="password"
              name="password"
              value={password}
              onChange={e => setPassword(e.target.value)}
            />
          </div>
          {!isLogin && (
            <div className="input-group">
              <label htmlFor="confirmPassword">CONFIRM PASSWORD</label>
              <input
                type="password"
                id="confirmPassword"
                name="confirmPassword"
                value={confirmPassword}
                onChange={e => setConfirmPassword(e.target.value)}
              />
            </div>
          )}
          <button type="submit">{isLogin ? 'LOG IN' : 'SIGN UP'}</button>
          <button 
            type="button" 
            className="toggle-btn" 
            onClick={() => {
              setIsLogin(!isLogin);
              setStatus(''); // Reset status message
            }}
          >
            {isLogin ? 'Create a new account' : 'Go to login'}
          </button>
        </form>
        {status && <p className="status-message">{status}</p>}
      </div>
    </div>
  );
};

export default SignupApp;
