import React, { useState } from 'react';
import './Profile.css';

const Profile = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [profile, setProfile] = useState({
    name: '',
    empId: '',
    email: '',
    phone: '',
    department: '',
    photo: null,
  });

  // Function to handle file input change and update state
  const handlePhotoChange = (e) => {
    if (e.target.files.length > 0) {
      const fileReader = new FileReader();
      fileReader.readAsDataURL(e.target.files[0]);
      fileReader.onload = (event) => {
        setProfile({ ...profile, photo: event.target.result });
      };
    }
  };

  // Function to handle text input change and update state
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfile({ ...profile, [name]: value });
  };

  // Function to toggle editing mode
  const toggleEdit = () => {
    setIsEditing((edit) => !edit);
  };

  // Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    setIsEditing(false);
    // Here you would handle the form submission to the backend
    console.log(profile);
  };

  return (
    <div className="profile-page">
      <aside className="sidebar"> {/* Your Sidebar Component */}</aside>
      <main className="profile-content">
        <div className="profile-card">
          {isEditing ? (
            <>
              <input type="file" onChange={handlePhotoChange} className="photo-input" />
              <img src={profile.photo} alt="Profile" className="profile-photo-preview" />
            </>
          ) : (
            <div className="profile-photo" style={{ backgroundImage: `url(${profile.photo})` }} />
          )}
          <div className="profile-info">
            <div className="name-edit">
              {isEditing ? (
                <input
                  type="text"
                  name="name"
                  value={profile.name}
                  onChange={handleInputChange}
                  placeholder="Name"
                  className="name-input"
                />
              ) : (
                <h1 className="profile-name">{profile.name || "Your Name"}</h1>
              )}
              <span onClick={toggleEdit} className="edit-toggle">
                {isEditing ? 'Save' : 'Edit'}
              </span>
            </div>
            <span className="status-indicator"></span>
            {new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
          </div>
          {isEditing ? (
            <form className="edit-form" onSubmit={handleSubmit}>
              <input type="text" name="empId" value={profile.empId} onChange={handleInputChange} placeholder="Employee ID" />
              <input type="email" name="email" value={profile.email} onChange={handleInputChange} placeholder="Email" />
              <input type="tel" name="phone" value={profile.phone} onChange={handleInputChange} placeholder="Phone" />
              <input type="text" name="department" value={profile.department} onChange={handleInputChange} placeholder="Department" />
              <button type="submit" className="save-btn">Save Changes</button>
            </form>
          ) : (
            <div className="profile-details">
              <p>Employee ID: {profile.empId}</p>
              <p>Email: {profile.email}</p>
              <p>Phone: {profile.phone}</p>
              <p>Department: {profile.department}</p>
            </div>
          )}
        </div>
      </main>
    </div>
  );
};

export default Profile;
