import React, { useState } from 'react';
import './Profile.css';

const Profile = () => {
  const [profileData, setProfileData] = useState({
    name: '',
    empId: '',
    email: '',
    phone: '',
    department: '',
    photo: ''
  });
  const [editMode, setEditMode] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfileData(prev => ({ ...prev, [name]: value }));
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setProfileData(prev => ({ ...prev, photo: reader.result }));
    };
    reader.readAsDataURL(file);
  };

  const handleEdit = () => {
    setEditMode(true);
  };

  const handleSave = (e) => {
    e.preventDefault();
    setEditMode(false);
    // Here, implement saving data to the server
    console.log(profileData);
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-photo-container">
          {profileData.photo ? (
            <img src={profileData.photo} alt="Profile" className="profile-photo" />
          ) : (
            <div className="profile-photo-placeholder"></div>
          )}
          {editMode && (
            <input
              type="file"
              onChange={handlePhotoChange}
              className="photo-upload-input"
            />
          )}
        </div>
        {editMode ? (
          <input
            name="name"
            value={profileData.name}
            onChange={handleInputChange}
            placeholder="Your Name"
            className="profile-name-input"
          />
        ) : (
          <>
            <h1 className="profile-name">{profileData.name || "Your Name"}</h1>
            <button onClick={handleEdit} className="edit-btn">Edit</button>
          </>
        )}
        <div className="profile-status">
          <span className="status-indicator"></span>
          Active
        </div>
        <div className="profile-time">{new Date().toLocaleTimeString()}</div>
      </div>
      <form onSubmit={handleSave} className="profile-info">
        {/* Editable Fields */}
        {/* ... */}
        {editMode && <button className="save-btn">Save</button>}
      </form>
    </div>
  );
};

export default Profile;
