import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Sidebar from './Sidebar';
import Dashboard from './Dashboard';
import SignupApp from './SignupApp';
// ... other imports

// Yeh naya component hai jo aapki main layout ko handle karega
const MainLayout = () => {
  const location = useLocation(); // Ab yeh yahaan use hoga
  const noSidebarRoutes = ['/']; // Yeh bata raha hai ki kaun se routes pe sidebar nahi chahiye

  return (
    <>
      {!noSidebarRoutes.includes(location.pathname) && <Sidebar />}
      <Routes>
        <Route path="/" element={<SignupApp />} />
        <Route path="/dashboard" element={<Dashboard />} />
        {/* ... other routes */}
      </Routes>
    </>
  );
};

function App() {
  return (
    <Router>
      <Sidebar />
      <Routes>
        <Route path="/" element={<SignupApp />} />  
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/tasks" element={<Tasks />} />
        <Route path="/teams" element={<Teams />} />
        <Route path="/MyClients" element={<Myclients />} />
      </Routes>
    </Router>
  );
}

export default App;
