import React, { useState } from 'react';
import './Teams.css';

const Teams = () => {
  const [messages, setMessages] = useState([
    { id: 1, author: 'Sharon S', content: 'How are the designs coming along?', type: 'received' },
    // ...other initial messages
  ]);
  const [newMessage, setNewMessage] = useState('');

  const sendMessage = (content, isFile = false) => {
    const nextId = messages.length + 1;
    setMessages([
      ...messages,
      {
        id: nextId,
        author: 'You',
        content,
        type: 'sent',
        isFile,
      },
    ]);
  };

  const handleSend = () => {
    if (newMessage.trim() !== '') {
      sendMessage(newMessage);
      setNewMessage('');
    }
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter' && newMessage.trim() !== '') {
      handleSend();
    }
  };

  const handleFileUpload = (event) => {
    const uploadedFiles = event.target.files;
    if (uploadedFiles.length) {
      const newFiles = Array.from(uploadedFiles).map((file) => ({
        name: file.name,
        size: file.size,
        url: URL.createObjectURL(file),
        isFile: true,
      }));

      newFiles.forEach((file) => {
        sendMessage(`Uploaded ${file.name}`, true);
      });
    }
  };

  return (
    <div className="chat-container">
      {/* Tabs and File Input */}
      {/* ... */}
      
      {/* Chat Messages */}
      <div className="chat-messages">
        {messages.map((message) => (
          <div key={message.id} className={`message ${message.type}`}>
            {/* ... */}
          </div>
        ))}
      </div>
      
      {/* Chat Input */}
      <div className="chat-input">
        <input
          type="text"
          placeholder="type something ..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          onKeyPress={handleKeyPress}
        />
        <button className="send-btn" onClick={handleSend}>Send</button>
      </div>
      
      {/* Hidden file input for triggering the file upload dialog */}
      <input
        id="fileUpload"
        type="file"
        multiple
        onChange={handleFileUpload}
        style={{ display: 'none' }}
      />
    </div>
  );
};

export default Teams;
