import React, { useState } from 'react';
import './Profile.css';

const Profile = () => {
  const [profile, setProfile] = useState({
    name: '',
    empId: '',
    email: '',
    phone: '',
    department: '',
    photo: '',
  });

  const [editMode, setEditMode] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfile({ ...profile, [name]: value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    
    reader.onloadend = () => {
      setProfile({ ...profile, photo: reader.result });
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleEditToggle = () => {
    setEditMode(!editMode);
  };

  return (
    <div className="profile-wrapper">
      <div className="profile-card">
        <div className="profile-photo-container">
          {profile.photo ? (
            <img src={profile.photo} alt="Profile" className="profile-photo" />
          ) : (
            <div className="profile-photo-placeholder"></div>
          )}
          {editMode && (
            <label htmlFor="imageUpload" className="upload-photo-btn">Upload Photo</label>
          )}
          <input 
            id="imageUpload" 
            type="file" 
            onChange={handleImageChange} 
            className="image-input" 
          />
        </div>
        <div className="profile-info">
          {editMode ? (
            <input
              type="text"
              name="name"
              value={profile.name}
              onChange={handleInputChange}
              placeholder="Your Name"
            />
          ) : (
            <h1>{profile.name || "Your Name"}</h1>
          )}
          <div className="status-section">
            <span className="status-indicator"></span> Active
            <span className="local-time">
              {new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
            </span>
          </div>
          <div className="edit-toggle" onClick={handleEditToggle}>
            {editMode ? 'Save' : 'Edit'}
          </div>
          {editMode && (
            <>
              <input
                type="text"
                name="empId"
                value={profile.empId}
                onChange={handleInputChange}
                placeholder="Employee ID"
              />
              <input
                type="email"
                name="email"
                value={profile.email}
                onChange={handleInputChange}
                placeholder="Email"
              />
              <input
                type="tel"
                name="phone"
                value={profile.phone}
                onChange={handleInputChange}
                placeholder="Phone"
              />
              <input
                type="text"
                name="department"
                value={profile.department}
                onChange={handleInputChange}
                placeholder="Department"
              />
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
