import React, { useState } from 'react';
import './Profile.css';

const Profile = () => {
  const [editMode, setEditMode] = useState(false);
  const [profile, setProfile] = useState({
    name: 'Vikash Dubey',
    empId: 'f,vf333333333333',
    email: 'vikash7618@gmail.com',
    phone: '09146030932',
    department: 'IT',
    photo: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfile({ ...profile, [name]: value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setProfile({ ...profile, photo: e.target.result });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleToggleEdit = () => {
    setEditMode(!editMode);
  };

  const handleSave = (e) => {
    e.preventDefault();
    setEditMode(false);
    // Implement save logic
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-photo-container">
          {profile.photo ? (
            <img src={profile.photo} alt="Profile" className="profile-photo" />
          ) : (
            <div className="profile-photo"></div>
          )}
          {editMode && (
            <>
              <input
                type="file"
                accept="image/*"
                onChange={handleImageChange}
                className="photo-upload-input"
              />
              <label htmlFor="photo-upload" className="upload-photo-btn">
                Upload Photo
              </label>
            </>
          )}
        </div>
        <div className="name-edit-container">
          {editMode ? (
            <input
              type="text"
              id="name"
              value={profile.name}
              name="name"
              onChange={handleInputChange}
              placeholder="Your Name"
              className="profile-name-input"
            />
          ) : (
            <h1 className="profile-name">{profile.name}</h1>
          )}
          <span className="edit-link" onClick={handleToggleEdit}>{editMode ? 'Save' : 'Edit'}</span>
        </div>
        <div className="status-section">
          <span className="status-indicator"></span>
          Active
          <span className="local-time">
            {new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
          </span>
        </div>
      </div>
      {editMode && (
        <form onSubmit={handleSave} className="profile-info">
          <input
            type="text"
            value={profile.empId}
            name="empId"
            onChange={handleInputChange}
            placeholder="Employee ID"
            className="profile-input"
          />
          <input
            type="email"
            value={profile.email}
            name="email"
            onChange={handleInputChange}
            placeholder="Email"
            className="profile-input"
          />
          <input
            type="tel"
            value={profile.phone}
            name="phone"
            onChange={handleInputChange}
            placeholder="Phone"
            className="profile-input"
          />
          <input
            type="text"
            value={profile.department}
            name="department"
            onChange={handleInputChange}
            placeholder="Department"
            className="profile-input"
          />
          <button type="submit" className="save-btn">Save</button>
        </form>
      )}
    </div>
  );
};

export default Profile;
