// Tasks.js
import React, { useState } from 'react';

const Task = ({ name, status, dueDate, remaining }) => {
  const statusColors = {
    'In progress': 'red',
    'Needs review': 'orange',
    'Needs attention': 'blue',
    'Needs input': 'yellow',
    'Planned': 'green',
  };

  const progressBarStyle = {
    height: '10px',
    width: '100%',
    backgroundColor: '#ddd',
  };

  const progressStyle = {
    height: '100%',
    width: status === 'Planned' ? '0%' : '50%', // This is just a placeholder, replace with actual logic
    backgroundColor: statusColors[status],
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '10px' }}>
      <input type="checkbox" />
      <div style={{ flex: 3 }}>{name}</div>
      <div style={progressBarStyle}>
        <div style={progressStyle}></div>
      </div>
      <div>{dueDate}</div>
      <div>{remaining}</div>
    </div>
  );
};

const Tasks = () => {
  const [tasks, setTasks] = useState([
    { name: 'Marketing strategy', status: 'In progress', dueDate: '4d', remaining: '-04:00' },
    // Add more task objects here with name, status, dueDate, and remaining properties
  ]);

  return (
    <div style={{ maxWidth: '600px', margin: 'auto' }}>
      <h1>High Priority</h1>
      {tasks.map((task, index) => (
        <Task key={index} {...task} />
      ))}
    </div>
  );
};

export default Tasks;
