import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import './SignupApp.css';

const SignupApp = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [signUpStatus, setSignUpStatus] = useState('');

  const handleSignUp = async (event) => {
    event.preventDefault();
    setSignUpStatus('Signing up...');

    try {
      const response = await fetch('http://localhost:3001/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      if (!response.ok) {
        if (response.status === 409) {
          throw new Error('User already exists');
        }
        throw new Error(`Signup failed: ${response.status}`);
      }

      // const data = await response.text(); // Removed as it's unused
      setSignUpStatus('Signup successful!');
      navigate('/S'); // Redirects to the dashboard
    } catch (error) {
      setSignUpStatus(error.message);
    }
  };

  return (
    <div className="app">
      <div className="illustration" />
      <div className="form-container">
        <form className="signup-form" onSubmit={handleSignUp}>
          <div className="input-group">
            <label htmlFor="username">USERNAME</label>
            <input
              type="text"
              id="username"
              name="username"
              value={username}
              onChange={e => setUsername(e.target.value)}
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">PASSWORD</label>
            <input
              type="password"
              id="password"
              name="password"
              value={password}
              onChange={e => setPassword(e.target.value)}
            />
          </div>
          <button type="submit">SIGN UP</button>
          <button type="button" className="decline-btn">NO THANKS</button>
        </form>
        {signUpStatus && <p className="signup-status">{signUpStatus}</p>}
      </div>
    </div>
  );
};

// Assuming you have other components like Dashboard
const Dashboard = () => {
  // Your Dashboard component code here
  return <div>Dashboard</div>;
};

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<SignupApp />} />
        <Route path="/Dashboard" element={<Dashboard />} />
        {/* You can add more routes here */}
      </Routes>
    </Router>
  );
}

export default App;
