import React, { useState } from 'react';
import './Profile.css';

const Profile = () => {
  const [profileData, setProfileData] = useState({
    name: '',
    empId: '',
    email: '',
    phone: '',
    department: '',
    photo: null
  });
  const [isEditing, setIsEditing] = useState(false);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setProfileData({ ...profileData, [name]: value });
  };

  const handleImageChange = (event) => {
    if (event.target.files && event.target.files[0]) {
      let img = event.target.files[0];
      setProfileData({ ...profileData, photo: URL.createObjectURL(img) });
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setIsEditing(false);
    // Logic to handle profile update
  };

  return (
    <div className="profile-container">
      <div className="profile-photo-section">
        {profileData.photo ? (
          <img src={profileData.photo} alt="Profile" className="profile-photo" />
        ) : (
          <div className="profile-photo-placeholder"></div>
        )}
        {isEditing && (
          <input
            type="file"
            onChange={handleImageChange}
            className="photo-upload-input"
          />
        )}
      </div>

      <div className="profile-details">
        {isEditing ? (
          <input
            type="text"
            name="name"
            value={profileData.name}
            onChange={handleInputChange}
            placeholder="Enter your name"
            className="profile-input"
          />
        ) : (
          <h2 className="profile-name">{profileData.name || "Your Name"}</h2>
        )}

        <div className="profile-status">
          <span className="status-indicator"></span> Active
        </div>

        <div className="profile-time">
          {new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
        </div>

        {isEditing ? (
          <>
            <input
              type="text"
              name="empId"
              value={profileData.empId}
              onChange={handleInputChange}
              placeholder="Employee ID"
              className="profile-input"
            />
            <input
              type="email"
              name="email"
              value={profileData.email}
              onChange={handleInputChange}
              placeholder="Email"
              className="profile-input"
            />
            <input
              type="tel"
              name="phone"
              value={profileData.phone}
              onChange={handleInputChange}
              placeholder="Phone"
              className="profile-input"
            />
            <input
              type="text"
              name="department"
              value={profileData.department}
              onChange={handleInputChange}
              placeholder="Department"
              className="profile-input"
            />
          </>
        ) : (
          <>
            <p className="profile-text">Employee ID: {profileData.empId}</p>
            <p className="profile-text">Email: {profileData.email}</p>
            <p className="profile-text">Phone: {profileData.phone}</p>
            <p className="profile-text">Department: {profileData.department}</p>
          </>
        )}

        <button onClick={() => setIsEditing(!isEditing)} className="edit-button">
          {isEditing ? 'Save' : 'Edit'}
        </button>
      </div>
    </div>
  );
};

export default Profile;
