import React, { useState, useEffect } from 'react';
import './Teams.css';

const Teams = () => {
  const [messages, setMessages] = useState([]);
  const [files, setFiles] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    // Immediately fetch messages when the component mounts
    const fetchMessages = () => {
      fetch('http://localhost:3001/getMessages')
        .then((response) => {
          if (!response.ok) {
            throw new Error('Network response was not ok');
          }
          return response.json();
        })
        .then((fetchedMessages) => {
          setMessages(fetchedMessages);
        })
        .catch((error) => {
          console.error('Error fetching messages:', error);
        });
    };

    fetchMessages();
  }, []);

  const handleSend = () => {
    if (!newMessage.trim() && files.length === 0) {
      return;
    }

    // Construct a new message object
    const messageData = {
      // The server would typically assign an ID
      author: 'You',
      content: newMessage,
      files: files.map(file => ({
        name: file.name,
        size: file.size,
        url: file.url, // This URL is created locally for preview purposes
      })),
      type: 'sent',
    };

    // Send the message data to the server
    fetch('http://localhost:3001/storeMessage', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(messageData),
    })
    .then(response => response.json())
    .then(data => {
      console.log('Success:', data);
      fetchMessages(); // Re-fetch messages to update the UI
    })
    .catch(error => {
      console.error('Error:', error);
    });

    setNewMessage(''); // Reset the input field
    setFiles([]); // Clear the files
  };

  const handleFileUpload = (event) => {
    const uploadedFiles = event.target.files;
    const newFiles = Array.from(uploadedFiles).map(file => ({
      name: file.name,
      size: file.size,
      url: URL.createObjectURL(file),
    }));

    setFiles(newFiles);
  };

  // Render the messages, handling files separately
  return (
    <div className="chat-container">
      <div className="chat-messages">
        {messages.map((message) => (
          <div key={message.id} className={`message ${message.type}`}>
            <div className="initials">
              {message.author.split(' ').map(name => name[0]).join('')}
            </div>
            <div className="text">
              {message.content}
              {message.files && message.files.map((file, index) => (
                <div key={index} className="file-item">
                  {file.name} - {Math.round(file.size / 1024)} KB
                  <a href={file.url} target="_blank" rel="noopener noreferrer" download>
                    Open
                  </a>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
      <div className="chat-input">
        <input
          type="text"
          placeholder="Type something..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
        />
        <button className="send-btn" onClick={handleSend}>Send</button>
        <input
          id="fileUpload"
          type="file"
          multiple
          onChange={handleFileUpload}
          style={{ display: 'none' }}
        />
        <button onClick={() => document.getElementById('fileUpload').click()}>Upload File</button>
      </div>
    </div>
  );
};

export default Teams;
