import React, { useState } from 'react';
import './Profile.css';

const Profile = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [profileData, setProfileData] = useState({
    name: '',
    empId: '',
    email: '',
    phone: '',
    department: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfileData({ ...profileData, [name]: value });
  };

  const handleSave = () => {
    setIsEditing(false);
    // Save data to server here...
  };

  const toggleEditMode = () => {
    setIsEditing(true);
  };

  const localTime = new Date().toLocaleTimeString();

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-picture">
          <div className="profile-photo"></div>
          {isEditing && <button className="upload-button">Upload Photo</button>}
        </div>
        <div className="profile-details">
          {isEditing ? (
            <input 
              value={profileData.name} 
              onChange={handleChange} 
              name="name"
              placeholder="Your Name" 
            />
          ) : (
            <h1 onClick={toggleEditMode}>{profileData.name || "Your Name"}</h1>
          )}
          <span className="status-indicator"></span>
          <span>Active</span>
          <span className="local-time">{localTime}</span>
        </div>
      </div>
      {isEditing ? (
        <div className="profile-edit-form">
          <input 
            value={profileData.empId} 
            onChange={handleChange} 
            name="empId" 
            placeholder="Employee ID" 
          />
          <input 
            value={profileData.email} 
            onChange={handleChange} 
            name="email" 
            placeholder="Email" 
          />
          <input 
            value={profileData.phone} 
            onChange={handleChange} 
            name="phone" 
            placeholder="Phone" 
          />
          <input 
            value={profileData.department} 
            onChange={handleChange} 
            name="department" 
            placeholder="Department" 
          />
          <button onClick={handleSave}>Save</button>
        </div>
      ) : (
        <div className="profile-info">
          <p>Employee ID: {profileData.empId}</p>
          <p>Email: {profileData.email}</p>
          <p>Phone: {profileData.phone}</p>
          <p>Department: {profileData.department}</p>
        </div>
      )}
    </div>
  );
};

export default Profile;
