import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import './SignupApp.css';

const SignupApp = () => { // Component name ko App se SignupApp mein change kiya
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [signUpStatus, setSignUpStatus] = useState('');
  const navigate = useNavigate(); // useNavigate hook ko initialize karein

  const handleSignUp = (event) => {
    event.preventDefault();
    setSignUpStatus('Signing up...');

    fetch('http://localhost:3001/signup', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    })
    .then(response => {
      if (!response.ok) {
        if (response.status === 409) {
          throw new Error('User already exists');
        }
        throw new Error(`Signup failed: ${response.status}`);
      }
      return response.text();
    })
    .then(data => {
      setSignUpStatus('Signup successful!');
      navigate('/dashboard'); // Signup successful hone par /dashboard par navigate karein
    })
    .catch(error => {
      setSignUpStatus(error.message);
    });
  };

  return (
    <div className="app">
      <div className="illustration" />
      <div className="form-container">
        <form className="signup-form" onSubmit={handleSignUp}>
          {/* Input fields and buttons */}
        </form>
        {signUpStatus && <p className="signup-status">{signUpStatus}</p>}
      </div>
    </div>
  );
};

export default SignupApp; // Corrected export
