import React, { useState } from 'react';
import './Teams.css';

const Teams = () => {
  const [messages, setMessages] = useState([
    // ... existing messages
  ]);
  const [files, setFiles] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const handleSend = () => {
    // ... existing send message logic
  };

  const handleFileUpload = (event) => {
    const uploadedFiles = event.target.files;
    if (uploadedFiles.length) {
      const newFiles = Array.from(uploadedFiles).map((file) => ({
        name: file.name,
        size: file.size,
        url: URL.createObjectURL(file),
      }));
      setFiles([...files, ...newFiles]);
    }
  };

  const handleReply = (message) => {
    // ... existing reply logic
  };

  return (
    <div className="chat-container">
      <div className="tabs">
        <div className="tab active">Chats</div>
        <div className="tab" onClick={() => document.getElementById('fileUpload').click()}>Files</div>
      </div>
      <div className="chat-messages">
        {/* Render chat messages and reply buttons */}
        {/* ... */}
      </div>
      <div className="files-list">
        {files.map((file, index) => (
          <div key={index} className="file-item">
            {file.name} - {Math.round(file.size / 1024)} KB
            <a href={file.url} target="_blank" rel="noopener noreferrer" download>
              Open
            </a>
          </div>
        ))}
      </div>
      <div className="chat-input">
        <input 
          type="text" 
          placeholder="type something ..." 
          value={newMessage} 
          onChange={(e) => setNewMessage(e.target.value)} 
          onKeyPress={(e) => e.key === 'Enter' && handleSend()} 
        />
        <button className="send-btn" onClick={handleSend}>Send</button>
      </div>
      {/* Hidden file input for triggering the file upload dialog */}
      <input 
        type="file" 
        id="fileUpload" 
        multiple 
        onChange={handleFileUpload} 
        style={{ display: 'none' }} 
      />
    </div>
  );
};

export default Teams;
