import React, { useState } from 'react';
import './Teams.css';

const Teams = () => {
  const [messages, setMessages] = useState([
    { id: 1, author: 'Sharon S', content: 'How are the designs coming along?', type: 'received' },
    // ...other initial messages
  ]);
  const [files, setFiles] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const sendMessage = (content, isFile = false) => {
    const nextId = messages.length + 1;
    setMessages([
      ...messages,
      {
        id: nextId,
        author: 'You',
        content,
        type: 'sent',
        isFile,
      },
    ]);
  };

  const handleSend = () => {
    if (newMessage.trim() !== '') {
      sendMessage(newMessage);
      setNewMessage('');
    }
  };

  const handleFileUpload = (event) => {
    const uploadedFiles = event.target.files;
    const fileList = Array.from(uploadedFiles).map((file) => ({
      name: file.name,
      size: file.size,
      url: URL.createObjectURL(file),
    }));

    setFiles([...files, ...fileList]);

    // For each uploaded file, send it as a message in the chat
    fileList.forEach((file) => {
      sendMessage(`Uploaded ${file.name}`, true);
    });
  };

  // ...rest of the component

  return (
    <div className="chat-container">
      {/* ...tabs and other elements */}
      
      <div className="chat-messages">
        {messages.map((message) => (
          <div key={message.id} className={`message ${message.type}`}>
            {message.isFile ? (
              <div className="file-message">
                <a href={message.content} target="_blank" rel="noopener noreferrer" download>
                  {message.content}
                </a>
              </div>
            ) : (
              <>
                <div className="initials">{message.author.split(' ').map((name) => name[0]).join('')}</div>
                <div className="text">{message.content}</div>
                {message.type === 'received' && (
                  <button className="reply-btn">Reply</button>
                )}
              </>
            )}
          </div>
        ))}
      </div>
      
      <div className="chat-input">
        {/* ...input and send button */}
      </div>
      
      <input
        id="fileUpload"
        type="file"
        multiple
        onChange={handleFileUpload}
        style={{ display: 'none' }}
      />
    </div>
  );
};

export default Teams;
