import React, { useState, useEffect } from 'react';
import './Teams.css';

const Teams = () => {
  const [messages, setMessages] = useState([]); // Initially empty
  const [files, setFiles] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    // Fetch messages from the server when the component mounts
    fetch('http://localhost:3001/getMessages')
      .then(response => response.json())
      .then(data => {
        setMessages(data); // Update state with fetched messages
      })
      .catch(error => {
        console.error('Error fetching messages:', error);
      });
  }, []);

  const handleSend = () => {
    if (newMessage.trim() !== '' || files.length > 0) {
      // Assume a unique ID can be generated for the new message
      const nextId = messages.length > 0 ? Math.max(...messages.map(m => m.id)) + 1 : 1;
      let fileInformation = files.map(file => ({
        name: file.name,
        size: file.size,
        url: file.url,
      }));

      const messageData = {
        id: nextId,
        author: 'You',
        content: newMessage,
        files: fileInformation, // Include file information as an array
        type: 'sent',
      };

      // Update the local state to include the new message
      setMessages(prevMessages => [...prevMessages, messageData]);
      setNewMessage('');
      setFiles([]);

      // Send the message data to the server
      fetch('http://localhost:3001/storeMessage', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(messageData),
      })
      .then(response => response.json())
      .then(data => console.log('Message stored:', data))
      .catch(error => console.error('Failed to store message:', error));
    }
  };

  const handleFileUpload = (event) => {
    const uploadedFiles = event.target.files;
    const fileList = Array.from(uploadedFiles).map(file => ({
      name: file.name,
      size: file.size,
      url: URL.createObjectURL(file), // Create a URL for the file
    }));
    setFiles(fileList); // Update the files state
  };

  const handleReply = (author) => {
    setNewMessage(`Replying to ${author}: `);
  };

  return (
    <div className="chat-container">
      {/* ...other components... */}

      <div className="chat-messages">
        {messages.map((message, index) => (
          <div key={index} className={`message ${message.type}`}>
            {/* ... */}
            <div className="text">
              {message.content}
              {message.files && message.files.map((file, fileIndex) => (
                <div key={fileIndex} className="file-item">
                  {file.name} - {Math.round(file.size / 1024)} KB
                  {/* This assumes file.url is a valid URL that can be accessed */}
                  <a href={file.url} target="_blank" rel="noopener noreferrer" download>
                    Open
                  </a>
                </div>
              ))}
            </div>
            {/* ... */}
          </div>
        ))}
      </div>
      
      {/* ...input and file upload components... */}
    </div>
  );
};

export default Teams;
