import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './signup.css';

const Signup = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const [signUpStatus, setSignUpStatus] = useState('');

  const handleSignUp = async (event) => {
    event.preventDefault();
    setSignUpStatus('Signing up...');

    // Here you would typically send a request to your backend server to create a new account
    // For now, let's just log to the console and pretend the account was created
    console.log('Sign Up:', { username, email, password });

    // Simulating a successful sign up and redirect to the login page
    setSignUpStatus('Account created successfully!');
    navigate('/login'); // Redirect to login page after sign up
  };

  return (
    <div className="signup-app">
      <div className="signup-illustration" />
      <div className="form-container">
        <form className="signup-form" onSubmit={handleSignUp}>
          <div className="input-group">
            <label htmlFor="email">EMAIL</label>
            <input
              type="email"
              id="email"
              name="email"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
          </div>
          <div className="input-group">
            <label htmlFor="username">USERNAME</label>
            <input
              type="text"
              id="username"
              name="username"
              value={username}
              onChange={e => setUsername(e.target.value)}
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">PASSWORD</label>
            <input
              type="password"
              id="password"
              name="password"
              value={password}
              onChange={e => setPassword(e.target.value)}
            />
          </div>
          <button type="submit" className="signup-btn">Create Account</button>
        </form>
        {signUpStatus && <p className="signup-status">{signUpStatus}</p>}
      </div>
    </div>
  );
};

export default Signup;
