import React, { useState, useEffect } from 'react';
import './Teams.css';

const Teams = () => {
  const [messages, setMessages] = useState([]);
  const [files, setFiles] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    fetch('http://localhost:3001/getMessages')
      .then(response => response.json())
      .then(fetchedMessages => setMessages(fetchedMessages))
      .catch(error => console.error('Error fetching messages:', error));
  }, []);

  const handleSend = () => {
    if (newMessage.trim() !== '' || files.length > 0) {
      const messageData = {
        author: 'You',
        content: newMessage,
        files: files.map(file => ({ name: file.name, size: file.size })),
        type: 'sent',
      };

      // Update the local state before sending to the server
      setMessages([...messages, messageData]);
      setNewMessage('');
      setFiles([]);

      // Send the message data to the server
      fetch('http://localhost:3001/storeMessage', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(messageData),
      })
      .then(response => response.json())
      .then(data => console.log('Success:', data))
      .catch(error => console.error('Error:', error));
    }
  };

  const handleFileUpload = (event) => {
    const uploadedFiles = event.target.files;
    const fileList = Array.from(uploadedFiles).map(file => ({
      name: file.name,
      size: file.size,
      url: URL.createObjectURL(file),
    }));
    setFiles(fileList);
  };

  const handleReply = (author) => {
    setNewMessage(`Replying to ${author}: `);
  };

  return (
    <div className="chat-container">
      {/* ... tabs and other elements ... */}
      <div className="chat-messages">
        {messages.map((message, index) => (
          <div key={index} className={`message ${message.type}`}>
            <div className="initials">
              {message.author.split(' ').map((name) => name[0]).join('')}
            </div>
            <div className="text">{message.content}</div>
            {/* Implement the reply button if needed */}
            <button className="reply-btn" onClick={() => handleReply(message.author)}>Reply</button>
          </div>
        ))}
      </div>
      <div className="chat-input">
        <input
          type="text"
          placeholder="Type something..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
        />
        <button className="send-btn" onClick={handleSend}>Send</button>
      </div>
      {/* Render uploaded files */}
      <div className="files-list">
        {files.map((file, index) => (
          <div key={index} className="file-item">
            {file.name} - {Math.round(file.size / 1024)} KB
            <a href={file.url} target="_blank" rel="noopener noreferrer" download>
              Open
            </a>
          </div>
        ))}
      </div>
      <input
        id="fileUpload"
        type="file"
        multiple
        onChange={handleFileUpload}
        style={{ display: 'none' }}
      />
      <div className="tab" onClick={() => document.getElementById('fileUpload').click()}>Upload File</div>
    </div>
  );
};

export default Teams;
