// Tasks.js
import React from 'react'; // Make sure this is the only React import
import Task from './Task';
import './Tasks.css'; // Assuming you have a CSS file for styling your components

const tasksData = [
  {
    name: 'Marketing strategy',
    status: 'In progress',
    progress: 50,
    dueDate: '4 d',
    remaining: '-04:00'
  },
  // ... Add more tasks as needed
];

const Tasks = () => {
  return (
    <div>
      <h1>Tasks</h1>
      <div className="task-list">
        {tasksData.map((task, index) => (
          <Task key={index} task={task} />
        ))}
      </div>
    </div>
  );
};

export default Tasks;
