import React, { useState } from 'react';
import './Teams.css';

const Teams = () => {
  const [messages, setMessages] = useState([
    { id: 1, author: 'Sharon S', content: 'How are the designs coming along?', type: 'received' },
    { id: 2, author: 'Rishi K', content: 'Good!', type: 'received' },
    // ...other initial messages
  ]);
  const [files, setFiles] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const handleSend = () => {
    if (newMessage.trim() !== '' || files.length > 0) {
      // Calculate nextId based on the existing messages
      const nextId = messages.length > 0 ? Math.max(...messages.map(message => message.id)) + 1 : 1;
      let messageContent = newMessage;
  
      // Handle files here as needed
      // ...
  
      // Construct the new message object
      const messageData = { id: nextId, author: 'You', content: messageContent, type: 'sent' };
  
      // Add the new message to the local state
      setMessages(prevMessages => [...prevMessages, messageData]); // Now using setMessages
      setNewMessage('');
  
      // Send the message data to the server
      fetch('http://localhost:3001/storeMessage', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(messageData), // Send messageData as the payload
      })
      .then(response => response.json())
      .then(data => console.log('Success:', data))
      .catch((error) => {
          console.error('Error:', error);
      });
    }
  };
  

  const handleFileUpload = (event) => {
    const uploadedFiles = event.target.files;
    const fileList = Array.from(uploadedFiles).map((file) => ({
      name: file.name,
      size: file.size,
      url: URL.createObjectURL(file),
    }));
    setFiles([...files, ...fileList]);
  };

  const handleReply = (author) => {
    setNewMessage(`Replying to ${author}: `);
  };

  return (
    <div className="chat-container">
      <div className="tabs">
        <div className="tab active">Chats</div>
        <div className="tab" onClick={() => document.getElementById('fileUpload').click()}>Files</div>
      </div>
      <div className="chat-messages">
        {messages.map((message) => (
          <div key={message.id} className={`message ${message.type}`}>
            <div className="initials">{message.author.split(' ').map((name) => name[0]).join('')}</div>
            <div className="text">{message.content}</div>
            {message.type === 'received' && (
              <button className="reply-btn" onClick={() => handleReply(message.author)}>Reply</button>
            )}
          </div>
        ))}
      </div>
      <div className="files-list">
        {files.map((file, index) => (
          <div key={index} className="file-item">
            {file.name} - {Math.round(file.size / 1024)} KB
            <a href={file.url} target="_blank" rel="noopener noreferrer" download>
              Open
            </a>
          </div>
        ))}
      </div>
      <div className="chat-input">
        <input
          type="text"
          placeholder="type something ..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSend()}
        />
        <button className="send-btn" onClick={handleSend}>Send</button>
      </div>
      <input
        id="fileUpload"
        type="file"
        multiple
        onChange={handleFileUpload}
        style={{ display: 'none' }}
      />
    </div>
  );
};

export default Teams;
