import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Sidebar from './Sidebar';
import Dashboard from './Dashboard';
import SignupApp from './SignupApp';
import Tasks from './Tasks';
import Teams from './Teams';
import Myclients from './MyClients';

// Yeh Layout component hai jo decide karega ki Sidebar kab dikhana hai
const Layout = () => {
  const location = useLocation();
  const showSidebar = location.pathname !== '/'; // Sidebar nahi dikhana hai jab path '/' ho

  return (
    <>
      {showSidebar && <Sidebar />} // Sidebar ko conditional render karna hai
      <Routes>
        <Route path="/" element={<SignupApp />} />
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/tasks" element={<Tasks />} />
        <Route path="/teams" element={<Teams />} />
        <Route path="/myclients" element={<Myclients />} />
      </Routes>
    </>
  );
};

function App() {
  return (
    <Router>
      <Sidebar />
      <Routes>
        <Route path="/" element={<SignupApp />} />  
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/tasks" element={<Tasks />} />
        <Route path="/teams" element={<Teams />} />
        <Route path="/MyClients" element={<Myclients />} />
      </Routes>
    </Router>
  );
}

export default App;
