import React, { useState } from 'react';
import './Tasks.css';

const initialTasks = [
  // Your initial tasks data...
];

const Tasks = () => {
  // Your useState and functions...

  return (
    <div className="task-container">
      <h1>High Priority</h1>
      <div className="task-list">
        {tasks.map(task => (
          <div key={task.id} className="task-item">
            <div className="checkbox-container">
              <input type="checkbox" id={`task-${task.id}`} checked={task.status === 'Completed'} onChange={() => toggleStatus(task.id)} />
              <label htmlFor={`task-${task.id}`}></label>
            </div>
            <div className={`task-info ${task.status.replace(/\s+/g, '-').toLowerCase()}`}>
              <span className="task-name">{task.name}</span>
              <span className="task-status">{task.status}</span>
              <span className="task-dueDate">{task.dueDate}</span>
              <span className="task-remaining">{task.remaining}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Tasks;
