import React, { useState } from 'react';
import './Profile.css';

const Profile = () => {
  const [userInfo, setUserInfo] = useState({
    name: '',
    email: '',
    phone: '',
    status: '',
    localTime: '',
  });

  const [isEditing, setIsEditing] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserInfo({ ...userInfo, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would usually send the data to the server
    console.log('Submitted Data', userInfo);
    setIsEditing(false); // Turn off editing mode after submission
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-photo-container">
          {/* Profile photo section */}
          <div className="profile-photo"></div>
          {/* Upload photo button will show only in edit mode */}
          {isEditing && <button className="upload-photo-btn">Upload Photo</button>}
        </div>
        {/* Conditionally render input or text for name */}
        {isEditing ? (
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={userInfo.name}
            onChange={handleChange}
            autoFocus
          />
        ) : (
          <h1>{userInfo.name || "Your Name"}</h1>
        )}

        {/* Edit button toggles editing mode */}
        <button onClick={() => setIsEditing(!isEditing)}>
          {isEditing ? 'Save' : 'Edit'}
        </button>
      </div>

      {/* Status and local time will show only in edit mode */}
      {isEditing && (
        <div className="status-container">
          <input
            type="text"
            name="status"
            placeholder="Status"
            value={userInfo.status}
            onChange={handleChange}
          />
          <input
            type="text"
            name="localTime"
            placeholder="Local Time"
            value={userInfo.localTime}
            onChange={handleChange}
          />
        </div>
      )}

      <div className="profile-contact-info">
        <form onSubmit={handleSubmit}>
          {/* Editable email field */}
          <div className="input-group">
            <label>Email Address</label>
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={userInfo.email}
              onChange={handleChange}
            />
          </div>

          {/* Editable phone field */}
          <div className="input-group">
            <label>Phone Number</label>
            <input
              type="tel"
              name="phone"
              placeholder="Phone"
              value={userInfo.phone}
              onChange={handleChange}
            />
          </div>

          {/* Save button only shows in edit mode */}
          {isEditing && <button type="submit">Save Changes</button>}
        </form>
      </div>
    </div>
  );
};

export default Profile;
