import React, { useState } from 'react';
import './.css';

const ChatInterface = () => {
  const [messages, setMessages] = useState([
    { id: 1, user: 'Sharon S', text: 'How are the designs coming along?' },
    { id: 2, user: 'Rishi K', text: 'Good!' },
    { id: 3, user: 'Anjali Kumar', text: 'Please go through all files before submissions' },
    // ... more messages
  ]);

  return (
    <div className="chat-interface">
      <div className="chat-header">
        <div className="chat-tab active">Chats</div>
        <div className="chat-tab">Files</div>
      </div>
      <div className="chat-messages">
        {messages.map(message => (
          <div key={message.id} className="chat-message">
            <div className="message-initials">{message.user.match(/\b(\w)/g).join('')}</div>
            <div className="message-text">{message.text}</div>
            <button className="reply-btn">Reply</button>
          </div>
        ))}
      </div>
      <div className="chat-input">
        <input type="text" placeholder="type something ..." />
        <button className="send-btn">Send</button>
      </div>
    </div>
  );
};

export default ChatInterface;
