import React, { useState } from 'react';
import './Profile.css';

const Profile = () => {
  const [editMode, setEditMode] = useState(false);
  const [profile, setProfile] = useState({
    name: '',
    empId: '',
    email: '',
    phone: '',
    department: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfile({ ...profile, [name]: value });
  };

  const handleToggleEdit = () => {
    setEditMode(!editMode);
  };

  const handleSave = (e) => {
    e.preventDefault();
    // Implement save logic
    setEditMode(false);
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-photo-container">
          {/* Placeholder or actual profile image */}
          <div className="profile-photo"></div>
          {/* Upload button only visible in edit mode */}
          {editMode && <button className="upload-photo-btn">Upload Photo</button>}
        </div>
        <div className="name-edit-container">
          {editMode ? (
            <input
              type="text"
              value={profile.name}
              name="name"
              onChange={handleInputChange}
              placeholder="Your Name"
            />
          ) : (
            <>
              <h1>{profile.name || "Your Name"}</h1>
              {/* Text link or icon to trigger edit mode */}
              <span className="edit-link" onClick={handleToggleEdit}>Edit</span>
            </>
          )}
        </div>
        <div className="status-section">
          {/* Active indicator */}
          <span className="status-indicator"></span>
          Active
          <span className="local-time">
            {new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
          </span>
        </div>
      </div>
      <form onSubmit={handleSave} className="profile-info">
        {/* Information fields */}
        <input
          type="text"
          value={profile.empId}
          name="empId"
          onChange={handleInputChange}
          placeholder="Employee ID"
          disabled={!editMode}
        />
        <input
          type="email"
          value={profile.email}
          name="email"
          onChange={handleInputChange}
          placeholder="Email"
          disabled={!editMode}
        />
        <input
          type="tel"
          value={profile.phone}
          name="phone"
          onChange={handleInputChange}
          placeholder="Phone"
          disabled={!editMode}
        />
        <input
          type="text"
          value={profile.department}
          name="department"
          onChange={handleInputChange}
          placeholder="Department"
          disabled={!editMode}
        />
        {/* Save button only shown in edit mode */}
        {editMode && <button type="submit" className="save-btn">Save</button>}
      </form>
    </div>
  );
};

export default Profile;
