import React, { useState } from 'react';
import './Profile.css';

const Profile = () => {
  const [editMode, setEditMode] = useState(false);
  const [profile, setProfile] = useState({
    name: '',
    empId: '',
    email: '',
    phone: '',
    department: '',
    photo: null,
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfile({ ...profile, [name]: value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setProfile({ ...profile, photo: e.target.result });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleToggleEdit = () => {
    setEditMode(!editMode);
  };

  const handleSave = (e) => {
    e.preventDefault();
    // Implement save logic
    setEditMode(false);
    // This is where you would typically handle the form submission,
    // for example, sending the profile data to the backend server.
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        {profile.photo ? (
          <img src={profile.photo} alt="Profile" className="profile-photo" />
        ) : (
          <div className="profile-photo-placeholder" />
        )}
        {editMode && (
          <input
            type="file"
            onChange={handleImageChange}
            className="photo-upload-input"
          />
        )}
        <div className="name-edit-container">
          {editMode ? (
            <input
              type="text"
              className="name-input"
              value={profile.name}
              name="name"
              onChange={handleInputChange}
              placeholder="Your Name"
            />
          ) : (
            <h1 className="profile-name">{profile.name || "Your Name"}</h1>
          )}
          <span className="edit-link" onClick={handleToggleEdit}>
            {editMode ? 'Save' : 'Edit'}
          </span>
        </div>
        <div className="status-section">
          <span className="status-indicator"></span>
          Active
          <span className="local-time">
            {new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
          </span>
        </div>
      </div>
      <div className="profile-info">
        <input
          type="text"
          className="profile-input"
          value={profile.empId}
          name="empId"
          onChange={handleInputChange}
          placeholder="Employee ID"
          disabled={!editMode}
        />
        <input
          type="email"
          className="profile-input"
          value={profile.email}
          name="email"
          onChange={handleInputChange}
          placeholder="Email"
          disabled={!editMode}
        />
        <input
          type="tel"
          className="profile-input"
          value={profile.phone}
          name="phone"
          onChange={handleInputChange}
          placeholder="Phone"
          disabled={!editMode}
        />
        <input
          type="text"
          className="profile-input"
          value={profile.department}
          name="department"
          onChange={handleInputChange}
          placeholder="Department"
          disabled={!editMode}
        />
        {editMode && (
          <button onClick={handleSave} className="save-btn">Save</button>
        )}
      </div>
    </div>
  );
};

export default Profile;
