// Tasks.js
import React from 'react';
import './Tasks.css';

const taskData = [
  {
    id: 1,
    name: 'Marketing strategy',
    status: 'in-progress',
    progress: 50,
    dueDate: '4 d',
    remaining: '-04:00',
  },
  // ... more tasks
];

const getStatusClassName = (status) => {
  switch (status) {
    case 'in-progress': return 'in-progress';
    case 'needs-review': return 'needs-review';
    case 'needs-attention': return 'needs-attention';
    case 'needs-input': return 'needs-input';
    case 'planned': return 'planned';
    default: return '';
  }
};

const Tasks = () => {
  return (
    <div className="tasks-container">
      <div className="task-header">
        <h2>High priority</h2>
      </div>
      {taskData.map(task => (
        <div className="task-row" key={task.id}>
          <input type="checkbox" className="task-checkbox" />
          <div className="task-name">{task.name}</div>
          <span className={`task-status status-tag ${getStatusClassName(task.status)}`}>
            {task.status.replace('-', ' ')}
          </span>
          <div className="progress-bar">
            <div className={`progress ${getStatusClassName(task.status)}`} style={{ width: `${task.progress}%` }}></div>
          </div>
          <div className="task-due-date">{task.dueDate}</div>
          <div className="task-remaining">{task.remaining}</div>
        </div>
      ))}
      <div className="task-footer">
        {taskData.length} tasks
      </div>
    </div>
  );
};

export default Tasks;
