import React, { useState } from 'react';
import './Profile.css';

const Profile = () => {
  const [profile, setProfile] = useState({
    name: '',
    empId: '',
    email: '',
    phone: '',
    department: '',
    photo: null,
  });
  const [editMode, setEditMode] = useState(false);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setProfile(prev => ({ ...prev, [name]: value }));
  };

  const handleImageChange = (event) => {
    if (event.target.files && event.target.files[0]) {
      let reader = new FileReader();
      reader.onload = (e) => {
        setProfile(prev => ({ ...prev, photo: e.target.result }));
      };
      reader.readAsDataURL(event.target.files[0]);
    }
  };

  const handleToggleEdit = () => {
    setEditMode(!editMode);
  };

  const handleSave = (event) => {
    event.preventDefault();
    // Implement save logic
    console.log(profile);
    setEditMode(false);
  };

  return (
    <div className="profile-wrapper">
      <div className="profile-card">
        <div className="profile-image-section">
          {profile.photo ? (
            <img src={profile.photo} alt="Profile" className="profile-image" />
          ) : (
            <div className="profile-image-placeholder"></div>
          )}
          {editMode && <input type="file" onChange={handleImageChange} />}
        </div>
        <div className="profile-info-section">
          <div className="profile-name-status">
            {editMode ? (
              <input 
                type="text"
                name="name"
                value={profile.name}
                onChange={handleInputChange}
                placeholder="Your Name"
              />
            ) : (
              <h1>{profile.name || "Your Name"}</h1>
            )}
            <span className="status-indicator"></span>Active
            <span className="profile-time">{new Date().toLocaleTimeString()}</span>
          </div>
          {editMode ? (
            <>
              <input
                type="text"
                name="empId"
                value={profile.empId}
                onChange={handleInputChange}
                placeholder="Employee ID"
              />
              <input
                type="email"
                name="email"
                value={profile.email}
                onChange={handleInputChange}
                placeholder="Email"
              />
              <input
                type="tel"
                name="phone"
                value={profile.phone}
                onChange={handleInputChange}
                placeholder="Phone"
              />
              <input
                type="text"
                name="department"
                value={profile.department}
                onChange={handleInputChange}
                placeholder="Department"
              />
            </>
          ) : (
            <>
              <p>Employee ID: {profile.empId}</p>
              <p>Email: {profile.email}</p>
              <p>Phone: {profile.phone}</p>
              <p>Department: {profile.department}</p>
            </>
          )}
          <button onClick={editMode ? handleSave : handleToggleEdit}>
            {editMode ? 'Save' : 'Edit'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Profile;
