import React, { useState } from 'react';
import './Teams.css'; // Make sure to create a Teams.css file with the provided styles

const Teams = () => {
  const [messages, setMessages] = useState([
    { id: 1, author: 'Sharon S', content: 'How are the designs coming along?', type: 'received' },
    { id: 2, author: 'Rishi K', content: 'Good!', type: 'received' },
    { id: 3, author: 'You', content: "It's deployed and available for verification", type: 'sent' },
    { id: 4, author: 'Anjali Kumar', content: 'Please go through all files before submissions', type: 'received' },
  ]);
  const [newMessage, setNewMessage] = useState('');

  const handleSend = () => {
    if (newMessage.trim() !== '') {
      const nextId = messages.length + 1;
      setMessages([...messages, { id: nextId, author: 'You', content: newMessage, type: 'sent' }]);
      setNewMessage('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSend();
    }
  };

  return (
    <div className="chat-container">
      <div className="tabs">
        <div className="tab active">Chats</div>
        <div className="tab">Files</div>
      </div>
      <div className="chat-messages">
        {messages.map((message) => (
          <div key={message.id} className={`message ${message.type}`}>
            <div className="initials">{message.author.split(' ').map(name => name[0]).join('')}</div>
            <div className="text">{message.content}</div>
            <div className="reply-btn">Reply</div>
          </div>
        ))}
      </div>
      <div className="chat-input">
        <input type="text" placeholder="type something ..." value={newMessage} onChange={(e) => setNewMessage(e.target.value)} onKeyPress={handleKeyPress} />
        <button className="send-btn" onClick={handleSend}>Send</button>
      </div>
    </div>
  );
};

export default Teams;
