// Tasks.js
import React from 'react';
import './Tasks.css';

// This is a mock data array, in a real application this could come from an API call.
const taskData = [
  {
    id: 1,
    name: 'Marketing strategy',
    status: 'In progress',
    dueDate: '4 d',
    remaining: '-04:00',
    progress: 50,
  },
  {
    id: 2,
    name: 'New contract template',
    status: 'Needs review',
    dueDate: 'Tomorrow',
    remaining: '-00:30',
    progress: 20,
  },
  // ... add more tasks as needed
];

const Tasks = () => {
  return (
    <div className="tasks">
      <h1>High priority</h1>
      {taskData.map((task) => (
        <div key={task.id} className="task">
          <input type="checkbox" />
          <div className="task-name">{task.name}</div>
          <div className="progress-bar">
            <div className={`progress ${task.status.replace(' ', '-').toLowerCase()}`} style={{ width: `${task.progress}%` }}></div>
          </div>
          <div className="due-date">{task.dueDate}</div>
          <div className="remaining">{task.remaining}</div>
        </div>
      ))}
    </div>
  );
};

export default Tasks;
